var Deformer = require('./index');

var login_form_fields = [
  {name: 'username', label: 'User Name'},
  {name: 'password', type: 'password'}
];

var login_form_config = {
    action: '/user/login'
};

var login_form = new Deformer({username: 'bob'}, login_form_fields, login_form_config);

console.log(login_form.render());

var props_form_fields = [
    'name',
    'email',
    {name: 'settings', type: 'checkbox', options: [{value: 'recieve mail'},{value: 'show email address'}] },
    
]

var prop_form_config = {action: '/set_props'};

var prop_form = new Deformer({email: 'bob@tom.com'}, props_form_fields, prop_form_config);

console.log(prop_form.render());