var string_utils = require('util/string');

module.exports = function(options_list) {
  //  console.log(__filename, options_list);
    options_list.forEach(function(option, index) {
        if (typeof(option) == 'string') {
            option = options_list[index] = {
                value: option
            };
        }
        if (!option.hasOwnProperty('label')){
            option.label = string_utils.title_case(option.value);
        }
    });
    
    return options_list;
}