var UID = function () {
    throw "UID cannot be instantiated";
}

/**
 * @property _nextID
 * @type Number
 * @protected
 **/
UID._nextID = 0;

/**
 * Returns the next unique id.
 * @method get
 * @return {Number} The next unique id
 * @static
 **/
UID.get = function () {
    return UID._nextID++;
}

module.exports = UID;