module.exports = function(data, config) {
    if (!config) {
        config = {};
    }
    if (!config.mode) {
        config.mode = 'color';
    };

    var self = this;
    var colors = [];
    var lp = 0;
    function _colorize(v, k) {
        colors.push(self._height_to_color(v, k, config));
    }
    data.forEach(_colorize);

    return colors;
}