

module.exports = function(samp, buckets) {
        var min_samp = Math.min.apply(Math, samp);
        var max_samp = Math.max.apply(Math, samp);

        var min_range = Math.floor(this.avg - (1.5 * this.std_dev));
        var max_range = Math.ceil( this.avg + (1.5 * this.std_dev));
        
        var bb = Math.ceil((3 * this.std_dev) / buckets);
        
        console.log('bb: ', bb);

        bins = [{
            up_to: min_range,
            gt: min_samp - 1,
            count: 0
        }];

        var last_inc = min_range;
        for (var div = min_range + bb; div < max_range; div += bb) {
            bins.push({
                up_to: div,
                gt: last_inc,
                count: 0
            });
            last_inc = div;
        }
        var last_bin;
        
        console.log('bins: ', bins);

        bins.push({
            up_to: max_samp + 1,
            gt: last_inc,
            count: 0
        });

        return bins;
    }