var import_module = require('./import');

module.exports = function(callback) {
    var self = this;
    import_module(this.data_file.image_file, this.get_rows(), this.get_cols(),  function(err, data) {
        console.log('read ', data.length, ' data rows; expected ', self.get_rows());
        self.data = data;
        this.data_read = true;
        callback(null, self);
    })
}