var color_module = require('util/colors/scale');
var scale = null;

/**
 * returns an array of colors that form a smooth range from sea to mountaintop
 */
module.exports = function() {
    if (!scale) {

        var scale = [];

        scale.push(color_module.measure(-5000,   0,   0,  0));
        scale.push(color_module.measure(-1000,   75,   0, 100));
        scale.push(color_module.measure(-200,    0,   0, 200));
        scale.push(color_module.measure(-50,     0, 125, 255));
        scale.push(color_module.measure(-10,      0, 255, 255));
        scale.push(color_module.measure(-1,      80, 255, 150));
        scale.push(color_module.measure( 0,     255, 255, 200));
        scale.push(color_module.measure( 300,   255, 200, 150));
        scale.push(color_module.measure( 800,   255, 100,  60));
        scale.push(color_module.measure( 1500,  200,  60,  30));
        scale.push(color_module.measure( 2000,  180,  50,  30));
        scale.push(color_module.measure( 4000,  150,  75, 120));
        scale.push(color_module.measure( 5000,  120,  80,  90));
        scale.push(color_module.measure( 10000,  80,  20,  70));
        scale.push(color_module.measure( 20000,  50,   0,  20));
        
        scale = color_module.scale(scale, 4);
    }

    return scale;
}