/**
 * i, j are units of tile chunks r, c
 */

module.exports = function(i, j) {
    var self = this;
    var img_data = [];

    var r = this.get_pixels_per_row() * i;
    var c = this.get_pixels_per_col() * j;
    var r_end = Math.min(this.get_rows(), r + this.get_pixels_per_row());
    var c_end = Math.min(this.get_cols(), c + this.get_pixels_per_col());

    console.log('rendering tile row ', r, ' ... ', r_end, ', col ', c, ' ... ', c_end);

    for (var r_sub = r; r_sub < r_end; ++r_sub) {
        img_data = img_data.concat(this.data[r_sub].slice(c, c_end));
    }

    return img_data;

}