var neobuffer = require('neobuffer');

module.exports = function (data){
    var buffer = neobuffer.Buffer(2 * data.length * data[0].length, 'binary');
    var count = 0;

    data.forEach(function(row){
        row.forEach(function(value){
            buffer.writeInt16(value, count, 'big');
            count += 2;
        })

    })

    return buffer;
}