var neobuffer = require('neobuffer');

module.exports = function (data){
    var buffer = neobuffer.Buffer(4 * data.length * data[0].length, 'binary');
    var count = 0;

    data.forEach(function(row){
        row.forEach(function(value){
            buffer.writeInt32(value, count, 'big');
            count += 4;
        })

    })

    return buffer;
}