var Canvas = require('canvas');

module.exports = function(shades, r, c) {

    var canvas = new Canvas(c, r);
    var ctx = canvas.getContext('2d');

    var id = ctx.createImageData(c, r);
    shades.forEach(function(shade, n) {
        var p = 4 * n;
        id.data[p] = id.data[p + 1] = id.data[p + 2] = shade;
        id.data[p + 3] = 255;
    });
    ctx.putImageData(id, 0, 0);

    return canvas;
}