var color_module = require('util/colors/scale');
var mars_scale = null;

module.exports = function() {
    if (!mars_scale) {

       var _scale = [];

        _scale.push(color_module.measure(-5000,    0,   0,  0));
        _scale.push(color_module.measure(-1000,   30,  40,  97));
        _scale.push(color_module.measure(-200,    17, 139, 174));
        _scale.push(color_module.measure(-25,     55, 229, 234));
        _scale.push(color_module.measure(-1,      85, 246, 179));
        _scale.push(color_module.measure( 0,     255, 201, 155));
        _scale.push(color_module.measure( 300,   217, 153, 99));
        _scale.push(color_module.measure( 1000,  210,  143,  54));
        _scale.push(color_module.measure( 2000,  166,  83,  12));
        _scale.push(color_module.measure( 4000,  98,  62,  38));
        _scale.push(color_module.measure( 8000,  114,  89, 79));
        _scale.push(color_module.measure( 16000,  196,  196,  196));
        _scale.push(color_module.measure( 24000,  204,  204,  204));
        _scale.push(color_module.measure( 36000,  255,   255,  255));

        mars_scale = color_module.scale(_scale, 8);
    }

    return mars_scale;
}