var color_module = require('util/colors/scale');
var mars_scale = null;

/**
 * This is a color scale - to colorize by height.
 */
module.exports = function() {
    if (!mars_scale) {

       var _scale = [];

        _scale.push(color_module.measure(-5000,    0,   0,    0));
        _scale.push(color_module.measure(-1000,   30,  40,   97));
        _scale.push(color_module.measure(-200,    17, 139,  174));
        _scale.push(color_module.measure(-25,     55, 229,  234));
        _scale.push(color_module.measure(-1,      85, 246,  179));
        _scale.push(color_module.measure( 0,     255, 201,  155));
        _scale.push(color_module.measure( 800,   217, 153,   99));
        _scale.push(color_module.measure( 1600,  210, 143,   54));
        _scale.push(color_module.measure( 2400,  166,  83,   12));
        _scale.push(color_module.measure( 3200,   98,  62,   38));
        _scale.push(color_module.measure( 6000,  114,  89,   79));
        _scale.push(color_module.measure( 12000, 210, 204,  196));
        _scale.push(color_module.measure( 24000, 225, 214,  204));
        _scale.push(color_module.measure( 36000, 255, 245,  225));

        mars_scale = color_module.scale(_scale, 8);
    }

    return mars_scale;
}