function Grid(data, rows, cols) {
    this.data = data;
    this.rows = arguments.length > 1 ? rows : data.length;
    this.cols = arguments.length > 2 ? cols : data[0].length;
}

Grid.prototype = {

    get_value:function (r, c) {
        if ((r < 0) || (r >= this.rows) || (c < 0) || (c >= this.cols)) {
            return null;
        }
        return this.data[r][c];
    },

    set_value:function (r, c, v) {
        if ((r < 0) || (r >= this.rows) || (c < 0) || (c >= this.cols)) {
            return null;
        }

        return this.data[r][c] = v;
    },

    spine_double:require('./spline_double'),

    reset:function () {
        var new_data = []
        for (var r = 0; r < this.rows; ++r) {
            var col = [];
            for (var c = 0; c < this.cols; ++c) {
                col.push(0);
            }
            new_data.push(col);
        }
        this.data = new_data;
    }

}

module.exports = Grid;