module.exports = function(self, request, response, next) {
    self.model.get(request.params.id, function(err, item) {
        if (err) {
            next(err);
        } else if (item) {
            self.model.delete(item._id, function(err, result) {
                if (err) {
                    next(err);
                } else {
                    self.post_destroy(request, response, item);
                }
            });
        } else {
            next(new Error('cannot get ' + self.name + ' ' + request.params.id + ' for deletion'));
        }
    });
}