var path_module = require('path');

module.exports = function (cont_module, action_name) {
    if (!cont_module.hasOwnProperty('_views')) {
        cont_module._views = {};
    }
    if (!cont_module._views.hasOwnProperty(action_name)) {
        var view_file = MVC_VIEWS + '/' + cont_module.name + '/' + action_name + '.html';

        if (!path_module.existsSync(view_file)) {
            view_file = MVC_VIEWS + '/scaffolding/' + action_name + '.html';
            if (!path_module.existsSync(view_file)) {
                switch (action_name) {
                case 'destroy', 'create':
                    break;
                default:
                    //  throw new Error('cannot find view file for module ');
                }
            }
        }

        cont_module._views[action_name] = view_file;
    }
}