module.exports = function(id) {
    if (this.string_id) {
        var out = id;
    } else {
        // for some reason requiring the below globally doesn't work here. 
        var mongo = require('mongodb');
        var BSON = mongo.BSONPure;
        if (typeof(id) != 'string') {
            var out = id;
        } else {
            var out = new BSON.ObjectID(id);
        }
    }
    return out;
}