/**
 * Note - put is a thin wrapper for save.
 */

module.exports = function(data, callback) {
    var self = this;

    if (this.config.coll) {
        if (data.hasOwnProperty('_id')) {
            this.config.coll.save(data, function() {
                self.config.coll.findOne({
                    _id: data._id
                }, callback);
            })
        } else {
            this.config.coll.save(data, callback);
        }
    } else if (callback) {
        callback(new Error('No Collection'));
    }
}