module.exports = function(data, callback) {
    var self = this;

    function _post_insert(err, result) {
        if (err) {
            console.log(__filename, ': put: error ', err);
        }
        callback(err, result);
    }

    if (this.config.coll) {
        if (data._id) {
            data._id = self._as_oid(data._id);
            self.get(data._id, function(err, result) {
                if (result) {
                    self.config.coll.save(data, _post_insert);
                } else {
                    self.config.coll.insert(data, _post_insert);
                }
            });
        } else {
            self.config.coll.insert(data, _post_insert);
        }
    } else {
        callback(new Error('No Collection'));
    }
}