module.exports = function(data, callback, filter, options) {
    if (this.config.coll) {
        if (!filter) {
            if (data._id) {
                filter = {
                    _id: data._id
                };
            } else {
                callback(new Error('attempt to update with no filter'));
            }
        }
        this.config.coll.update(filter, data, options, callback);
    } else {
        callback(new Error('No Connection'));
    }
}