var like = require('unit/alike');
var assert = require('assert');
var util = require('util');

module.exports = function(a, b, msg) {
    if (!like(a, b)) {
//        var at = util.inspect(a);
  //      var bt = util.inspect(b);
       // console.log("ALIKE FAIL: ", 'not alike: ' , at ,' ::: ' , bt);
        throw new assert.AssertionError({actual: b, expected: a, operator: 'alike', message: msg});
    }
}
