module.exports = function(colors, id) {
    var place = 0;
    for (var il = colors.length, i = 0; i < il; ++i) {
        id.data[place++] = colors[i][0];
        id.data[place++] = colors[i][1];
        id.data[place++] = colors[i][2];
        id.data[place++] = 255;
        if (i < 3) {
            console.log('color ', i, colors[i]);
        }
    }
}