var Canvas = require('canvas');
var fs = require('fs');

module.exports = function(canvas_image) {
    if (typeof canvas_image == 'string') {
        var buffer = fs.readFileSync(canvas_image);
        var img = new Canvas.Image();
        img.src = buffer;
        canvas_image = img;
    }

    var canvas = new Canvas(canvas_image.width, canvas_image.height);
    var ctx = canvas.getContext('2d');
    ctx.drawImage(canvas_image, 0, 0, canvas_image.width, canvas_image.height);
    return canvas;
}