var fs = require('fs');

module.exports = function(canvas, dest, callback) {
    var out = fs.createWriteStream(dest),
        stream = canvas.createPNGStream();

    stream.on('data', function(chunk) {
        out.write(chunk);
    });

    stream.on('end', function(){
        console.log(__filename, ': written ', dest);
        callback();
    });
}