
module.exports = {
    clamp: function (value, min, max, as_int){
        
        value = Math.max(min, Math.min(max, value));
        return (as_int) ? parseInt(value) : value;
    },
    
    random: function(min, max){
        var range = max - min;
        
        return parseInt(Math.random() * range + min); 
    },

    rescale: function(value, min1, max1, min2, max2, as_int){
        if (value <= min1){
            return as_int ? parseInt(min2) : min2;
        } else if (value >= max1){
            return as_int ? parseInt(max2) : max2;
        }

        var ratio = (value - min1)/ (max1 - min1);
        var rescaled = ((max2 - min2) * ratio) + min2;
        return as_int ? parseInt(rescaled) : rescaled;
    },


}