module.exports =
    function (mil, since) {

        if (since){
            mil = new Date().getTime() - mil;
        }
        mil /= 1000;
        mil = parseInt(mil);

        var mins = parseInt(mil / 60);
        var secs = mil % 60;

        if (mins) {
            return mins + ' mins, ' + secs + ' secs';
        } else {
            return secs + ' seconds';
        }
    }