function _compass(d, s) {
    var p = s.compass[d];
    
    if (p){
        return p.__toString();
    } else {
        return '--';
    }
}

module.exports = function(long_form) {

    var out = '';

    if (long_form) {
        out += '<table border="2">';
        out += '<tr>';
        out += '<td>&nbsp;</td>';
        out += '<td>N' + _compass('N', this) + '</td>';
        out += '<td>&nbsp;</td>';
        out += '</tr>';
        out += '<tr>';
        out += '<td>W' + _compass('W', this) + '</td>';
        out += '<td><b>i</b>: ' + this.i + ',<b>j</b>: ' + this.j + '</td>';
        out += '<td>E' + _compass('E', this) + '</td>';
        out += '</tr>';
        out += '<tr>';
        out += '<td>&nbsp;</td>';
        out += '<td>S' + _compass('S', this) + '</td>';
        out += '<td>&nbsp;</td>';
        out += '</tr>';
        out += '</table>';
    } else {
        out += '[i: ' + this.i + ', j ' + this.j + ']';
    }

    return out;

}