var lingo = require('lingo');
var en = lingo.en;

en.uncountable('admin'); // in the controller name context, admin doesn't pluralize. 
en.uncountable('meta');  // **
module.exports = function(str){
    if (en.isPlural(str)){
        return str;
    } else {
        return en.pluralize(str);
    }
}