module.exports = function(data) {
    var cache = {
        pos: [],
        neg: []
    }

    function _populate_cache(height, offset) {
        height = parseInt(height);
        var ah = (height < 0) ? -1 * height : height;
        if (height < 0) {
            if (cache.neg[ah]) {
                cache.neg[ah].push(offset);
            } else {
                cache.neg[ah] = [offset];
            }
        } else {
            if (cache.pos[ah]) {
                cache.pos[ah].push(offset);
            } else {
                cache.pos[ah] = [offset];
            }
        }
    }

    data.forEach(_populate_cache);

    return cache;
}