module.exports = {
    _opt_data: require('./_opt_data'),

    _next_tile: require('./_next_tile'),

    _opt_to_colors: require('./_opt_to_colors'),

    chop: require('/chop'),

    _measure_bins: require('./_measure_bins'),

    _fill_bins: require('./_fill_bins'),

    render_image_tile: require('./render_tile'),

    load: function() {

        require('./accessors').load();

        var MOLA = require('./index');

        if (!MOLA.extensions.hasOwnProperty('chopper') && MOLA.extensions.chopper) {

            for (var prop in module.exports) {
                if (prop != 'load') {
                    MOLA.prototype[prop] = module.exports[prop];
                }
            }

            MOLA.extensions.chopper = true;
        }

    }
}