var path = require('path');
var inflect = require('inflect');
var Context = require('mvc/context');

module.exports = function(controller, action) {
    
    return function(request, response, next) {
        
        var context = new Context({ controller: controller,
                                  action: action,
                                  request: request,
                                  response: response,
                                  next: next});
        
        context.handle();
    };
}
