module.exports = function(fields) {
    for (var i = 0; i < arguments.length; ++arguments) {
        // legacy test
        if (typeof(arguments[i]) == 'function') {
            throw new Error(__filename + ':: all() failed legacy check')
        }
    }

    q = this.soft_delete ? {
        deleted: {
            "$ne": true
        }
    } : {};
    return this.find(q, fields);
}