// note- options is a legacy property, and generates an error if present.
// this function returns a cursor. 
module.exports = function(query, fields, options) {

    if (this.config.coll) {
        if (options) {
            throw new Error(__filename + ': used options ' + JSON.stringify(options));
        }

        if (typeof(fields) == 'function') {
            throw new Error(__filename + '::bad fields ' + JSON.stringify(fields));
        }
        
        return this.config.coll.find(query, fields);

    } else {
        throw (new Error('No Connection'));
    }
}