module.exports = function(id, callback) {
    var self = this;

    function _delete(err, data) {
        if (err) {
            callback(err);
        } else if (!data) {
            callback(new Error('no record found'))
        } else if (self.soft_delete) {
            data.deleted = true;
            self.put(data, callback);
        } else {
            this.config.callback.delete(data, callback);
        }
    }

    this.get(id, _delete);
}