

module.exports = {
    /**
     *returns an array with one element in it - the concatenated versions of all arrays in a
     *you'll generally want to get [0] from the result. 
     */
    flatten_2d_array: function(a){
        if (a.length == 0){
            return [[]];
        } else if (a.length == 1){
            return a;
        } else if (a.length == 2) {
            return [a[0].concat(a[1])];
        } else {
            var a1 = a.slice(0, Math.ceil(a.length/2));
            var a2 = a.slice(a1.length);
            
            var a1_flat = module.exports.flatten_2d_array(a1);
            var a2_flat = module.exports.flatten_2d_array(a2);
            return module.exports.flatten_2d_array(a1_flat.concat(a2_flat));
        }
        
    }
}